mapper.select:
SELECT *
FROM sys_table
WHERE db_id = #{fid} and delete_time = 0;

SELECT *
FROM sys_table
WHERE id = #{id}
and delete_time = 0;

SELECT *
FROM sys_table
WHERE db_id = #{fid}
and name = #{name}
and delete_time = 0

mapper.update:
UPDATE sys_table
SET name=#{table.name},
storage_engine = #{table.storageEngine},
db_id = #{table.dbId}
WHERE id = #{table.id}
and delete_time = 0;

mapper.delete:
UPDATE sys_table
SET delete_time = current_timestamp()
WHERE id = #{id};

UPDATE sys_table
SET delete_time = current_timestamp()
WHERE db_id = #{fid};
